const fetch = require('node-fetch');
const url = 'https://promptshield-g1cz.onrender.com/v1/analyze';
const prompt_text = $Prompt?.trim();
const action_on_high_risk_input = $Action_on_High_Risk?.trim();
const compliance_frameworks_input = $Compliance_Frameworks?.trim();
const api_key_input = $API_Key?.trim();

let frameworks;

try {
  let cleaned = compliance_frameworks_input.replace(/\\/g, '');
  frameworks = JSON.parse(cleaned);
} catch (err) {
  frameworks = compliance_frameworks_input
    .replace(/\\/g, '')     
    .split(',')
    .map((s) => s.trim())
    .filter(Boolean);
}


const auth= 'Bearer '+api_key_input
const body = {
    text: prompt_text,
    compliance_frameworks: frameworks,
    action_on_high_risk: action_on_high_risk_input
};

const options = {
    method: 'POST',
    headers: {
        'Authorization': auth,
        'Content-Type': 'application/json',
    },
    body: JSON.stringify(body),
};

try {
    const response = await fetch(url, options);
    if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        if (errorData && errorData.detail) {
            throw new Error(errorData.detail);
        } else {
            throw new Error(`HTTP Error ${response.status}`);
        }
    }

    const data = await response.json();
    return {
        redacted_prompt: data.clean_text ?? '',
        risk_score: data.risk_score !== undefined ? String(data.risk_score) : '0',
        violations: JSON.stringify(data.violations ?? []),
    };

} catch (error) {
    console.error(error);
    return '';
}
